INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(56639, 'deDE', 'Nicht, wenn er uns erfasst! Warum bist du nicht geflohen? ... Warum hast du deine Hand in den Brunnen getaucht?', '', 60192),
(56615, 'deDE', '', 'Freut Euch nicht zu früh, kleine Welpen. Wir werden diese Welt von Euch bereinigen.', 60192),
(55658, 'deDE', '', 'Aus der Distanz müsst Ihr mehr als nur meine Äxte fürchten.', 60192),
(54910, 'deDE', 'Ruinen und Trümmer übersäen die Landschaft... und für mich sind die Attentäter von Rabenholdt Teil dieses Chaos.', '', 60192),
(54511, 'deDE', 'Wir gehen. Bleibt dicht bei mir.', '', 60192),
(54104, 'deDE', 'In Ordnung, dann brechen wir ihn für dich.', '', 60192),
(53963, 'deDE', 'Ihr habt ihn fast! Og\’nor ka Lok\’tar - Jetzt bringen wir es zu Ende!', '', 60192),
(53875, 'deDE', 'Dort oben, über uns!', '', 60192),
(53194, 'deDE', 'Vor Euch seht Ihr die Folgen der Herrschaft Todesschwinges. Der einstige Rubindrachenschrein ist zerstört, seine verkohlten Überreste und die verbrannte Erde das einzige Zeugnis seiner Existenz.\n$bEs heißt, vor langer Zeit hätte ein Tropfen von Alexstraszas Blut den Schnee geschmolzen und der Blutroten Eiche vor Euch Leben geschenkt. Ihre Zweige waren zum Himmel erhoben und strahlten die Wärme und das Leben des roten Drachenschwarms aus.\n$bDoch nun können ihre verzerrten und verrottenden Äste die Kraft des Lebens nicht länger nutzen - und wenden sich Tod und Zerstörung zu. Als die Flammen und die Verderbnis diesem Land die Lebenskraft nahmen, fing der Baum ein Bild davon ein... ein Echo der Krieger, die auf diesem heiligen Boden fielen.', '', 60192);
