UPDATE `broadcast_text` SET `Text`='I got something for you, $N. Looks important.', `Text1`='I got something for you, $N. Looks important.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8547;
UPDATE `broadcast_text` SET `Text`='We are all pieces of a much larger puzzle, $N. Just as you guide the piece of a puzzle to its destination, so too are we guided by another.$B$BAnd perhaps they who guide us are themselves guided by an even greater force.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8528;
UPDATE `broadcast_text` SET `Text`='You\'re shaping up nicely, $N. I\'ve heard good things...', `Text1`='You\'re shaping up nicely, $N. I\'ve heard good things...', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8495;
UPDATE `broadcast_text` SET `Text`='I see some interesting characters come and go here at the port. Watch yourself, $N. It\'s never clear who to trust, what with all of the shady activity that goes on here...', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8325;
