UPDATE `broadcast_text` SET `Text`='$N, how may I further your training in the eyes of the Earth Mother?', `Text1`='$N, how may I further your training in the eyes of the Earth Mother?', `LanguageID`=1, `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8293;
UPDATE `broadcast_text` SET `Text`='You know enough to be dangerous, but you still look like you need training to me $N.', `Text1`='You know enough to be dangerous, but you still look like you need training to me $N.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8289;
UPDATE `broadcast_text` SET `Text`='I can make amazing things with the materials I find out here. By Muradin\'s beard, there are fantastic creatures in these hills! Go have a whack at them, $N, and bring me back some tall tales.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8051;
UPDATE `broadcast_text` SET `Text1`='Cairne has brought us to a land rich with possibilities for the future. We have been fortunate, and so in turn we pass on our good fortune to others. That is why I began teaching, $N.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8050;
UPDATE `broadcast_text` SET `Text`='Your skills are coming along nicely. Soon you\'ll be running a leather shop of your own. I\'ll be glad for the help, $N. My hands are only so fast, you know? There\'s so much work to do!', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=8003;
UPDATE `broadcast_text` SET `Text`='Lots to do! Haven\'t been this busy since my cousin accidentally vaporized all the cloth and leather in Gnomeregan. My, it was drafty for a while! Ah, $N, those were the days....', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7999;
UPDATE `broadcast_text` SET `Text1`='Listen carefully, $N, for I have much knowledge to impart.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7986;
UPDATE `broadcast_text` SET `Text`='We live and die, and the land remains, eternal, unmoved by our passage. Thus, land cannot belong to us, $N. Instead, we belong to it. Therefore we protect the land, for without it we cannot hope to survive.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7969;
UPDATE `broadcast_text` SET `Text1`='You need to make your stitches smaller, $N. Fewer items will slip out of your quivers and ammo pouches. Besides, no one likes a lumpy tunic.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7954;
UPDATE `broadcast_text` SET `Text`='If you are seeking a truly rewarding pursuit, then a life of enchanting is for you!', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7907;
UPDATE `broadcast_text` SET `Text1`='Can you not sense the anguish of the Earth Mother for her lost children, the undead? Speak quickly, $N. Every second costs me dearly. We must help them find their way back to her!', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7744;
UPDATE `broadcast_text` SET `Text`='I\'m busy, $N. Tell Whuut to handle whatever it is. That young pup has been stewing all day long. He spends much more time like that, and he\'s going to hurt something.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7733;
UPDATE `broadcast_text` SET `Text`='Once, it is true, I was human. What might otherwise be a shortcoming works to our advantage, $N, for I know human weaknesses intimately.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7710;
UPDATE `broadcast_text` SET `Text`='Hi $N!  Are you here to buy something?  Or maybe sell something?$B$BYou won\'t find better prices anywhere, or my name isn\'t Augustus the Touched!', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7621;
UPDATE `broadcast_text` SET `Text`='A darkness had fallen over my eyes, but you tore me from my unholy reverie.  You saved me, $N.$B$BAnd now I beg you... to forgive me.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7455;
UPDATE `broadcast_text` SET `Text`='The battle is over, and the people of Darrowshire are saved.$B$BThey are saved, $N, because of you.  You are truly a hero of Darrowshire.', `Flags`=1, `VerifiedBuild`=60192 WHERE `VerifiedBuild`>0 AND `ID`=7454;
