INSERT INTO `quest_request_items` (`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES
(27992, 0, 0, 0, 0, 'Did that magnet contraption actually work?', 0), -- Magnets, How Do They Work?
(27972, 0, 0, 0, 0, 'Were you able to recover the rum?  I would try but I am deathly afraid of the water.  Not to mention that crazy shark.', 0), -- Boosting Morale
(27119, 0, 1, 0, 0, 'Just get me that Ogre Tannin, and I\'ll whip you up an ogre suit that will fool Captain Kromcrush into thinking you\'re one of his men.', 0), -- The Gordok Ogre Suit
(27051, 0, 1, 0, 0, 'Have you done as I\'ve asked of you? We must weed out whatever growth agent remains within their possession.', 0), -- Through Persistence
(27038, 6, 0, 0, 0, 'Eh wot?$B$BMy parts. Where are they? You can\'t build a tank without parts!$B$BIf yeh did, it\'d be small. An\' mostly inner-fectual.', 0), -- Missing Parts
(14457, 0, 0, 0, 0, 'Did you find anything of use on that agent?', 0), -- The Sunreaver Plan
(14443, 0, 0, 0, 0, 'What is it that you have there?', 0), -- The Battered Hilt
(26329, 1, 0, 0, 0, 'Welcome back, $N.', 0), -- One More Thing
(26222, 1, 0, 0, 0, 'Did you get those parts?', 0), -- Scrounging for Parts
(29685, 396, 0, 0, 0, 'Well, $c, I must admit, I wasn\'t sure I\'d see you again.', 0), -- Hard to Kill
(29262, 0, 0, 0, 0, 'This potent pile of voodoo witchery emanates strong mojo...', 0), -- Zul'Gurub Voodoo
(29261, 0, 0, 0, 0, 'The maker of this voodoo pile perished in the process.  Perhaps if you could finish it, the spirits would protect you instead....', 0), -- Zul'Aman Voodoo
(29153, 0, 0, 0, 0, 'Did you find something, $n?', 0), -- Booty Bay's Interests
(24796, 1, 0, 0, 0, 'Is it true that you have restored Quel\'Delar?', 0), -- A Victory For The Silver Covenant
(28807, 6, 0, 0, 0, 'What\'s this you\'ve brought me?', 0), -- Expert Opinion
(24553, 1, 0, 0, 0, 'What happened during your time in the Sunwell?', 0), -- The Purification of Quel'Delar
(24522, 1, 0, 0, 0, 'What brings you here?', 0), -- Journey To The Sunwell
(20438, 1, 0, 0, 0, 'Was Shandy able to help you get a Sunreaver tabard?', 0), -- A Suitable Disguise
(24454, 1, 0, 0, 0, 'We\'ve been expecting your return. What has kept you?', 0); -- Return To Caladis Brightspear

