INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(29516, 1, 1, 0, 0, 0, 0, 0, 0, 'Very nice work, $N. You\'ve quite a talent for this.', 59536), -- Keeping the Faire Sparkling
(29512, 1, 2, 0, 0, 0, 0, 0, 0, 'Thank you for your help, $N. It takes all of us to keep the faire up and running.', 59536), -- Putting the Carnies Back Together Again
(29518, 1, 1, 0, 0, 0, 0, 0, 0, 'There\'s a good bit of scrap here. Thanks for your help. It\'ll be a big help when I refit the steam tonks.$B$BPlease take this as a reward for your help.', 59536), -- Rearm, Reuse, Recycle
(30104, 2, 21, 0, 0, 0, 0, 0, 0, 'We rarely enjoy the opportunity to employ a brave $c like you. Our archive grows more thorough, thanks to your efforts.', 59185), -- Documenting the Timeways
(30103, 1, 1, 0, 0, 0, 0, 0, 0, 'I owe my life to you, heroes.$b$bNow that we hold the Dragon Soul safely within Wyrmrest, it may be that all of Azeroth owes their lives to you.', 59185), -- To Wyrmrest!
(30105, 1, 0, 0, 0, 0, 0, 0, 0, 'It is truly as we foresaw: Benedictus was the Twilight Prophet. And now he is no more.$b$bWe dealt a mighty bow to the Twilight\'s Hammer today. Let us hope that it was a deathblow.', 59185), -- The Twilight Prophet
(30102, 6, 0, 0, 0, 0, 0, 0, 0, 'You\'ve arrived.$b$bGood, let us begin.', 59185), -- The Hour of Twilight
(30101, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you for your help, stranger.$b$bYou fight well. I hope to fight with you again in the future.', 59185), -- The Path to the Dragon Soul
(30100, 1, 0, 0, 0, 0, 0, 0, 0, 'We are but moments away from the Dragon Soul.', 59185), -- The Vainglorious
(30099, 273, 1, 0, 0, 0, 0, 0, 0, 'Good. We march on.', 59185), -- In Unending Numbers
(30098, 1, 0, 0, 0, 0, 0, 0, 0, 'Let us proceed.', 59185), -- The Well of Eternity
(30096, 1, 0, 0, 0, 0, 0, 0, 0, 'Murozond is no more. The timeways are open to me once again.', 59185), -- Murozond
(30097, 1, 1, 0, 0, 0, 0, 0, 0, '<Alurmi collects the vessel from you.>$b$bI will handle these from here. Of course, they must be properly labeled, documented, and stored.', 59185); -- Archival Purposes
