INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(56639, 'ruRU', 'Он может поглотить и нас! Почему ты не бежал? Зачем ты опустил руку в Источник?', '', 60192),
(56615, 'ruRU', '', 'Не обольщайтесь, щенки. Мы избавим от вас этот мир.', 60192),
(55658, 'ruRU', '', 'В моем арсенале не только топоры!', 60192),
(54511, 'ruRU', 'Все, уходим. Не отставайте.', '', 60192),
(54104, 'ruRU', 'Тогда мы сделаем это за тебя.', '', 60192),
(53963, 'ruRU', 'Еще немного! Ог\'нор ка Лок\'тар! Покончим с ним!', '', 60192),
(53875, 'ruRU', 'Смотрите, там, наверху!', '', 60192),
(53194, 'ruRU', 'Перед вами – последствия царствия Смертокрыла. Лишь выжженная земля и обугленные корни напоминают о том, что здесь когда-то было Рубиновое святилище драконов.\n$bГоворят, когда-то одна капля крови Алекстразы растопила снег и даровала жизнь великому Багряному дубу, чьи ветви триумфально вздымались к небу и лучились теплом и жизнью алых драконов.\n$bНыне его гниющий, скрученный ствол более не может служить источником жизни и здоровья – и лишь смерть и разрушение обитают здесь. Пока пламя и скверна гасили жизненную силу этих земель, великое дерево впитывало в себя образы окружающего его кошмара – отзвуки духа воинов, павших на этой священной земле.', '', 60192);
