INSERT INTO `journal_encounter_section_locale` (`ID`, `locale`, `Title_lang`, `BodyText_lang`, `VerifiedBuild`) VALUES
(4262, 'ruRU', 'Кохром', 'Когда запас здоровья Морхока падает до 90%, он разрывает себя пополам, тем самым создавая своего близнеца, Кохрома. Его близнец применяет те же заклинания, что и сам Морхок, но с задержкой в несколько секунд.', 60192),
(3890, 'ruRU', 'Сокрушение доспеха', 'Морхок поражает цель, нанося $103687s1% урона и уменьшая броню цели на $103687s2% на последующие $103687d. Эффект суммируется до 10 раз.', 60192),
(3879, 'ruRU', 'Топот', 'Морхок с силой топает по земле, нанося игрокам в радиусе $103414A м $%103414s1 ед. урона, распределяемого между целями. Текущая цель Морхока и еще один союзник рядом с ней получают двойную долю урона.$[2,3\n\nВ героическом режиме также увеличивает физический урон, получаемый пораженными игроками, на $103414s3% на $103414d.$]', 60192);
